/* eslint-disable react-hooks/exhaustive-deps */
"use client";
import { signIn } from "next-auth/react";
import React, { useEffect, useState } from "react";
import { useSession } from "next-auth/react";
import { useRouter } from "next/navigation";

interface registrationProps {
  name: string;
  email: string;
  password: string;
  confirmpassword: string;
  image: string;
}

function SignUp() {
  const [register, setRegister] = useState<registrationProps>({
    name: "",
    email: "",
    password: "",
    confirmpassword: "",
    image:
      "data:image/png;base64,",
  });
  const [login, setLogin] = useState({
    name: "",
    password: "",
  });
  const { data: session } = useSession();
  const route = useRouter();
  const registerChangeHandler = (e: any) => {
    setRegister({ ...register, [e.target.name]: e.target.value });
  };

  const registerHandler = async () => {
    const { name, email, password, confirmpassword, image } = register;
    if (
      name &&
      email &&
      password &&
      confirmpassword &&
      image &&
      password == confirmpassword
    ) {
      const respons = await fetch(`${process.env.NEXT_PUBLIC_NEXTAUTH_API_URL}/api/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application.json",
        },
        body: JSON.stringify({
          name,
          email,
          password,
          image,
        }),
      });
      console.log(respons)
      if(respons.ok){
        alert("User Register Success");
        const loginUser = await signIn("credentials",{
          redirect:false,
          name,
          password
        })
        console.log(loginUser)
        route.push("/")
      }else{
        if (!respons.ok) return alert("this User Alredy Exist");
      }
    } else {
      console.log("error");
    }
  };
  const loginChangeHandler = (e: any) => {
    setLogin({ ...login, [e.target.name]: e.target.value });
  };
  const loginHandler = async () => {
    const { name, password } = login;
    if (!name || !password) {
      console.log("inncorect");
    } else {
      try {
        const respons = await signIn("credentials", {
          name,
          password,
          redirect: false,
        });
        console.log(respons);
      } catch (error) {
        throw error;
      }
    }
  };

  useEffect(() => {
    if (session) {
      route.replace("/");
    }
  }, [session]);

  return (
    <div className="flex justify-center mt-36">
      {/* register */}
      <div className="border-2 p-5 flex flex-col text-center gap-5 rounded-xl">
        <h1 className="font-bold text-white text-xl">Registartion</h1>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Your UserName"
            type="username"
            value={register.name}
            name="name"
            onChange={registerChangeHandler}
          />
        </div>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Your Email"
            type="email"
            name="email"
            value={register.email}
            onChange={registerChangeHandler}
          />
        </div>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Your Password"
            type="Password"
            name="password"
            value={register.password}
            onChange={registerChangeHandler}
          />
        </div>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Confirm Your Password"
            type="password"
            name="confirmpassword"
            value={register.confirmpassword}
            onChange={registerChangeHandler}
          />
        </div>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Image Addres"
            name="image"
            value={register.image}
            onChange={registerChangeHandler}
          />
        </div>
        <button
          onClick={() => registerHandler()}
          className="bg-white p-2 rounded-xl "
        >
          Sign Up
        </button>
      </div>
      {/* login */}
      <div className="border-2 p-5 flex flex-col text-center gap-5 rounded-xl">
        <h1 className="font-bold text-white text-xl">Log In</h1>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Your UserName"
            type="username"
            value={login.name}
            name="name"
            onChange={loginChangeHandler}
          />
        </div>
        <div className="w-full">
          <input
            className="p-2 outline-none border-2 rounded-xl text-white"
            placeholder="Enter Your Password"
            type="Password"
            name="password"
            value={login.password}
            onChange={loginChangeHandler}
          />
        </div>
        <button
          onClick={() => loginHandler()}
          className="bg-white p-2 rounded-xl "
        >
          Log In
        </button>
      </div>
    </div>
  );
}

export default SignUp;
